import React from 'react';
import styled from 'styled-components';

const Container = styled.div`
  display: flex;
  justify-content: center;
  width: 100vw;
  height: 100vh;
  padding: 50px 0px;
`;

const Content = styled.div`
  width: 80vw;
  padding-bottom: 50px;
`;

const Title = styled.div`
  border-bottom: ${(props) => props.theme.pinkBorder};
  padding-left: 10px;
  padding-bottom: 20px;
  font-size: 25px;
`;

const Item = styled.div``;

const Image = styled.div`
  background-image: url(${(props) => props.bgUrl});
  background-repeat: no-repeat;
  background-size: contain;
  background-position: center center;
  height: 200px;
  width: 100%;
`;

const ExchangeOrRefund = (props) => {
  const imageUrl = props.location.state.imageUrl;
  const name = props.location.state.name;
  const brand = props.location.state.brand;
  const option = props.location.state.option;

  return (
    <Container>
      <Content>
        <Title>교환/환불처리하기</Title>
        <Item>
          <Image bgUrl={`${imageUrl}`} />
          {name}
          {brand}
          {option}
        </Item>
      </Content>
    </Container>
  );
};

export default ExchangeOrRefund;
