import React from 'react';
import styled from 'styled-components';
import PropTypes from 'prop-types';
import MyPurchasedSection from 'Components/Sections/MyPurchasedSection';
import Message from 'Components/Message';

const Container = styled.div`
  display: flex;
  justify-content: center;
  width: 100vw;
  padding-bottom: 30px;
`;

const Content = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  width: 90%;
`;

const Title = styled.div`
  border-bottom: ${(props) => props.theme.pinkBorder};
  width: 100%;
  padding: 20px 20px;
  margin: 30px 0px;
  font-size: 25px;
`;

const ItemNumContainer = styled.div`
  display: flex;
  justify-content: left;
  width: 100%;
`;

const ItemNum = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  width: 100px;
  padding: 15px;
  font-size: 12px;
  font-weight: 900;
`;

const ItemContainer = styled.div`
  display: flex;
  justify-content: left;
  flex-wrap: wrap;
  width: 90vw;
  margin: 40px 5vw;
`;

const MyPurchasedPresenter = ({ purchased, error }) => {
  return (
    <Container>
      <Content>
        <Title>구매했던 상품</Title>
        <ItemNumContainer>
          <ItemNum>총 {purchased.length}개</ItemNum>
        </ItemNumContainer>
        <ItemContainer>
          {purchased && purchased.length > 0 && (
            <>
              {purchased.map((item) => (
                <MyPurchasedSection
                  key={item.product.id}
                  id={item.product.id}
                  purchaseId={item.id}
                  imageUrl={item.product.product_image}
                  name={item.product.name}
                  brand={item.product.brand}
                  option={item.option}
                  price={item.product.price}
                />
              ))}
            </>
          )}
        </ItemContainer>
      </Content>
      {error && <Message color='e74c3c' text={error} />}
    </Container>
  );
};

MyPurchasedPresenter.propTypes = {
  like: PropTypes.array,
  error: PropTypes.string,
};

export default MyPurchasedPresenter;
