import React from 'react';
import { Link } from 'react-router-dom';
import PropTypes from 'prop-types';
import styled from 'styled-components';
import store from 'store';

const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  width: 23%;
  @media screen and (max-width: 768px) {
    width: 48%;
  }
  margin: 0 1%;
`;

const Item = styled.div`
  width: 100%;
  &:focus,
  &:hover {
    text-decoration: none;
    opacity: 0.7;
    transition: opacity 0.3s linear;
  }
`;

const Image = styled.div`
  background-image: url(${(props) => props.bgUrl});
  background-repeat: no-repeat;
  background-size: contain;
  background-position: center center;
  height: 200px;
  width: 100%;
`;

const BrandPrice = styled.div`
  display: flex;
  width: 100%;
  padding-bottom: 10px;
  margin-top: 10px;
  background-color: #f8f9fb;
  border-bottom: ${(props) => props.theme.mainBorder};
`;

const Brand = styled.div`
  font-size: 14px;
  font-weight: 500;
  opacity: 0.7;
  margin: 0px 10px;
`;
const Name = styled.div`
  font-size: 14px;
  font-weight: 500;
`;
const Price = styled.div`
  width: 100%;
  font-size: 14px;
  font-weight: 500;
  opacity: 0.8;
  padding: 10px;
`;

const SLink = styled(Link)`
  &:hover,
  &:focus {
    text-decoration: none;
    color: black;
    opacity: 0.8;
  }
  margin: 10px;
`;

const ReviewBtn = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  width: 80px;
  height: 24px;
  font-size: 13px;
  margin-top: 20px;
  border-radius: 3px;
  background-color: skyblue;
`;

const MyPurchasedSection = ({
  purchaseId,
  id,
  imageUrl,
  name,
  brand,
  price,
}) => {
  return (
    <Container>
      <SLink to={`/products/${id}`}>
        <Item>
          <Image bgUrl={`${imageUrl}`} />
          <BrandPrice>
            <Brand>[ {brand} ]</Brand>
            <Name>{name}</Name>
          </BrandPrice>
          <Price>{price}원</Price>
        </Item>
      </SLink>
      <SLink
        to={
          store.getState()?.user.logged
            ? {
                pathname: '/review',
                state: {
                  brand: brand,
                  item: name,
                  id: id,
                  purchaseId: purchaseId,
                },
              }
            : '/login'
        }
      >
        <ReviewBtn>구매평 작성</ReviewBtn>
      </SLink>
    </Container>
  );
};

MyPurchasedSection.propTypes = {
  id: PropTypes.number.isRequired,
  imageUrl: PropTypes.string,
  name: PropTypes.string,
  brand: PropTypes.string,
  price: PropTypes.string,
};

export default MyPurchasedSection;
