import React from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';

const Container = styled.div`
  display: flex;
  justify-content: center;
  width: 100vw;
  height: 100vh;
  padding: 50px 0px;
`;

const Content = styled.div`
  width: 80vw;
  padding-bottom: 50px;
`;

const Title = styled.div`
  border-bottom: ${(props) => props.theme.pinkBorder};
  padding-left: 10px;
  padding-bottom: 20px;
  font-size: 25px;
`;

const Item = styled.div`
  display: flex;
  justify-content: center;
`;

const Image = styled.div`
  background-image: url(${(props) => props.bgUrl});
  background-repeat: no-repeat;
  background-size: contain;
  background-position: center center;
  height: 200px;
  width: 100%;
`;

const SLink = styled(Link)``;

const ExchangeOrRefund = (props) => {
  const id = props.location.state.id;
  const imageUrl = props.location.state.imageUrl;
  const name = props.location.state.name;
  const brand = props.location.state.brand;
  const option = props.location.state.option;
  const price = props.location.state.price;
  const qty = props.location.state.qty;

  return (
    // <Container>
    //   <Content>
    //     <Title>교환/환불처리하기</Title>
    //     <Item>
    //       <Image bgUrl={`${imageUrl}`} />
    //       {name}
    //       {brand}
    //       {option}
    //     </Item>
    //   </Content>
    // </Container>
    <Item>
      <SLink to={`/products/${id}`}>
        <Image bgUrl={`${imageUrl}`} />
      </SLink>
      <Brand>{brand}</Brand>
      <Name>{name}</Name>
      <Option>{option}</Option>
      <Price>{price}</Price>
      <Qty>{qty}</Qty>
      <PostPrice>0</PostPrice>
      <TotalPrice>{price * qty}</TotalPrice>
    </Item>
  );
};

export default ExchangeOrRefund;
