import React, { useState, useEffect } from 'react';
import MyPagePresenter from './MyPagePresenter';
import { usersApi } from 'api';
import Loader from 'Components/Loader';

const MyPageContainer = () => {
  const [myPageData, setMyPageData] = useState({
    user: null,
    basket: null,
    like: null,
    purchase: null,
    exchange: null,
    refund: null,
  });
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);

  const getData = async () => {
    try {
      const { data: user } = await usersApi.user();
      const { data: basket } = await usersApi.basket();
      const { data: like } = await usersApi.favs();
      const { data: purchase } = await usersApi.purchasedList();
      const {
        data: { results: exchange },
      } = await usersApi.exchangedList();
      const {
        data: { results: refund },
      } = await usersApi.refundedList();
      setMyPageData({
        user: user,
        basket: basket,
        like: like,
        purchase: purchase,
        exchange: exchange,
        refund: refund,
      });
    } catch {
      setError("can't find anything");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    getData();
  }, []);

  const { user, basket, like, purchase } = myPageData;

  return loading ? (
    <Loader />
  ) : (
    <MyPagePresenter
      user={user}
      basket={basket}
      like={like}
      purchase={purchase}
      error={error}
    />
  );
};

export default MyPageContainer;
