import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import BasketPresenter from './BasketPresenter';
import { usersApi } from 'api';
import Loader from 'Components/Loader';
import store from 'store';

const BasketContainer = () => {
  const [basketData, setBasketData] = useState({
    user: null,
    basket: null,
    basketObjectId: false,
  });
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);

  const basketObjectId_props = useLocation().state;

  const getItems = async () => {
    const { data: user } = await usersApi.user();
    const { data } = await usersApi.basket(store.getState().user?.token);
    const basket = data.map((basket) => {
      basket.product.option = basket.option;
      basket.product.qty = basket.qty;
      basket.product.productId = basket.product.id;
      basket.product.id = basket.id;
      return basket.product;
    });
    console.log(user, basket);
    setBasketData({
      user: user,
      basket: basket,
      basketObjectId: false,
    });
    if (basketObjectId_props !== undefined) {
      setBasketData({
        user: user,
        basket: basket,
        basketObjectId: basketObjectId_props.basketObjectId,
      });
    }
  };

  const getData = async () => {
    try {
      await getItems();
    } catch {
      setError("Can't find basket items");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    getData();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const { user, basket, basketObjectId } = basketData;

  return loading ? (
    <Loader />
  ) : (
    <BasketPresenter
      user={user}
      basket={basket}
      basketObjectId={basketObjectId}
      error={error}
    />
  );
};

export default BasketContainer;
