import React from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';

const Container = styled.div`
  display: flex;
  justify-content: center;
`;

const Item = styled.div`
  display: flex;
  border-bottom: ${(props) => props.theme.mainBorder};
  width: 100%;
`;

const Image = styled.div`
  background-image: url(${(props) => props.bgUrl});
  background-repeat: no-repeat;
  background-size: contain;
  height: 150px;
  width: 15vw;
`;

const Brand = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  width: 10vw;
`;

const Name = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  width: 25vw;
`;

const Option = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  width: 5vw;
`;

const Price = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  width: 7vw;
`;

const Qty = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  width: 5vw;
`;

const PostPrice = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  width: 7vw;
`;

const TotalPrice = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  width: 7vw;
`;

const SLink = styled(Link)`
  &:hover {
    text-decoration: none;
    color: black;
    opacity: 0.7;
  }
`;

const ExchangeOrRefund = (props) => {
  const id = props.location.state.id;
  const imageUrl = props.location.state.imageUrl;
  const name = props.location.state.name;
  const brand = props.location.state.brand;
  const option = props.location.state.option;
  const price = props.location.state.price;
  const qty = props.location.state.qty;

  return (
    <Container>
      <Item>
        <SLink to={`/products/${id}`}>
          <Image bgUrl={`${imageUrl}`} />
        </SLink>
        <Brand>{brand}</Brand>
        <Name>{name}</Name>
        <Option>{option}</Option>
        <Price>{price}</Price>
        <Qty>{qty}</Qty>
        <PostPrice>0</PostPrice>
        <TotalPrice>{price * qty}</TotalPrice>
      </Item>
    </Container>
  );
};

export default ExchangeOrRefund;
