import React from 'react';
import { Link } from 'react-router-dom';
import PropTypes from 'prop-types';
import styled from 'styled-components';

// const Container = styled.div`
//   display: flex;
//   justify-content: center;
//   align-items: center;
//   width: 25%;
//   margin-bottom: 50px;
// `;

const Item = styled.div`
  width: 320px;
  &:focus,
  &:hover {
    text-decoration: none;
    opacity: 0.7;
    transition: opacity 0.3s linear;
  }
`;

const Image = styled.div`
  background-image: url(${(props) => props.bgUrl});
  background-repeat: no-repeat;
  background-size: contain;
  background-position: center center;
  height: 320px;
  width: 320px;
`;

const BrandPrice = styled.div`
  display: flex;
  width: 320px;
  padding-bottom: 10px;
  margin-top: 10px;
  border-bottom: ${(props) => props.theme.mainBorder};
`;

const Brand = styled.div`
  font-size: 14px;
  font-weight: 500;
  opacity: 0.7;
  margin: 0px 10px;
`;
const Name = styled.div`
  font-size: 14px;
  font-weight: 500;
`;
const Price = styled.div`
  width: 320px;
  font-size: 14px;
  font-weight: 500;
  opacity: 0.8;
  padding: 10px;
`;

const SLink = styled(Link)`
  &:hover,
  &:focus {
    text-decoration: none;
    color: black;
    opacity: 0.8;
  }
  margin: 10px;
`;

const PageFrameSection = ({ id, imageUrl, name, brand, price }) => {
  return (
    // <Container>
    <SLink to={`/products/${id}`}>
      <Item>
        <Image bgUrl={`${imageUrl}`} />
        <BrandPrice>
          <Brand>[ {brand} ]</Brand>
          <Name>{name}</Name>
        </BrandPrice>
        <Price>{price}Ïõê</Price>
      </Item>
    </SLink>
    /* </Container> */
  );
};

PageFrameSection.propTypes = {
  id: PropTypes.number.isRequired,
  imageUrl: PropTypes.string,
  name: PropTypes.string,
  brand: PropTypes.string,
  price: PropTypes.string,
};

export default PageFrameSection;
