import React, { useState, useEffect } from 'react';
import MyPurchasedPresenter from './MyPurchasedPresenter';
import { usersApi } from 'api';
import Loader from 'Components/Loader';

const MyPurchasedContainer = () => {
  const [purchasedData, setPurchasedData] = useState({
    purchased: null,
    exchange: null,
    refund: null,
  });
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);

  const getData = async () => {
    try {
      const { data: purchased } = await usersApi.purchasedList();
      const {
        data: { results: exchange },
      } = await usersApi.exchangedList();
      const {
        data: { results: refund },
      } = await usersApi.refundedList();
      setPurchasedData({
        purchased: purchased,
        exchange: exchange,
        refund: refund,
      });
    } catch {
      setError("Can't find anything.");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    getData();
  }, []);

  const { purchased, exchange, refund } = purchasedData;

  return loading ? (
    <Loader />
  ) : (
    <MyPurchasedPresenter
      purchased={purchased}
      exchange={exchange}
      refund={refund}
      error={error}
    />
  );
};

export default MyPurchasedContainer;
